// 목표! 최소값을 찾아라

function getMin(a, b) { // 함수선언 함수이름
    if (a > b) {
        return b
    } else {
        return a
    }
} 

function getMin2(a, b) { // 함수선언 함수이름
    if (a > b) {
        return b
    } // else가 꼭 필요한 곳이 있고 필요 없는 곳도 있다! 잘 구분햐
    return a
}

function getMin3(a, b) { // 함수선언 함수이름
    var min;
    if (a > b) {
        min = b
    } else {
        min = a
    }
    return min
} 

var min = getMin(15644,4894654)
console.log(min)

//////

function getMinFromArray(numbers) {
    var min = Infinity
    // 배열에서 최소값을 구하여 min에 할당
    for (num of numbers){
        if ( min > num) {
            min = num
        }
    }
    return min
}

var numbers= [1,2,3,4,5]
var min = getMinFromArray(numbers)
console.log(min)